# 1 -Combien y a-t-il de handle dans cette array ?
def h_handle()
  h_list = $list.length
  puts h_list
end


# 2 - Quel est le handle le plus court de cette liste ?
def short_handle
  short_list = $list.min_by(&:length)
  puts short_list
end


# 3 - Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
def car5_handle
  car5_list =[]
  list_lenth = $list.length
  list_lenth.times{ |i| car5_list.push $list[i].size-1 }
  puts car5_list.count(5)
  # journalists.count {|handle| handle.length == 6
end

# ///////////////////////////TO DOOOOOOOOOOOOOOOOOOOOOO///////////////////////
# 4 - Combien commencent par une majuscule (première lettre juste après le @) ?
def maj_handle
  puts $list.count {|handle| handle[1] =~/[[:upper:]]/}
end


# 5 - Trie la liste de handle par ordre alphabétique.
def alpha_handle
  puts $list.sort_by{|handle| handle[1].downcase}
  # padding = 4
  # alpha_list = $list.sort{|a,b|
  #   a,b = [a,b].map{|s| s.gsub(/\d+/){|m| "0"*(padding - m.size) + m } }
  #   a<=>b
  # }
  # puts alpha_list
end


# 6 - Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
def order_length_handle
  # order_length_list = $list.sort_by {|x| x.length}
  order_length_list = $list.sort_by(&:length)
  puts order_length_list
end


# 7 - Quelle est la position dans l'array de la personne @epenser ?
def epenser_pose_handle
  puts $list.index('@epenser')
end


# 8 - Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
def repart_handle
  ($list.max_by(&:length).size-1).times {|i| puts "#{i+1} dans #{$list.count {|handle| handle.size-1 == (i+1)}}"}
  # journalists.select {|handle| handle.size == 1}
  # into boucle dans taille max taille et min_by
end


def system
  while true
    puts "Enter : \n|1 - Combien y a-t-il de handle dans cette array ? \n|2 - Quel est le handle le plus court de cette liste ? \n|3 - Combien y-a-t'il de handle contenant 5 caractères ? \n|4 - Combien commencent par une majuscule ? \n|5 - Trier la liste de handle par ordre alphabétique. \n|6 - Trier la liste de handle par taille des handle. \n|7 - Quelle est la position dans l'array de la personne @epenser ? \n|8 - Sortir une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc) \n|0 - For leave "
    print ">"
    choice = gets.chomp
    case choice
      when "1"
        h_handle()
        puts "\ndone\n "
        next 
      when "2"
        short_handle()
        puts "\ndone\n "
        next 
      when "3"
        car5_handle()
        puts "\ndone\n "
        next 
      when "4"
        maj_handle()
        puts "\ndone\n "
        next 
      when "5"
        alpha_handle()
        puts "\ndone\n "
        next 
      when "6"
        order_length_handle()
        puts "\ndone\n "
        next 
      when "7"
        epenser_pose_handle()
        puts "\ndone\n "
        next 
      when "8"
        repart_handle()
        puts "\ndone\n "
        next 
      when "0"
        puts "\nGoodbye\n"
        break
      else
        puts "\nUnknow choice\n"
        next
    end
  end
end

system()

